/* An empty pie chart. */

let emptyChart = function() {
    c3.generate({
        data: {
            columns: [
                ["empty", 1],
            ],
            type : 'pie',
            colors: {
                "empty": "#808080"
            }
        }
    })
}

/* A dynamic pie chart generated by the user. */

let generateChart = function(wallet, marketStats) {

    // Generate data.
    let columns = []
    for (let coin in wallet) {
        let quantity = wallet[coin]
        let value = marketStats[coin].price_usd
        let symbol = marketStats[coin].symbol
        let column = [symbol, quantity * value]
        columns.push(column)
    }

    c3.generate({
        bindto: '#chart',
        data: {
            columns: columns,
            type: "pie"
        },
    });
}
